import { useState } from 'react';
import './App.css';
import CoinCard from './CoinCard'
import CoinData from './MOCK_DATA.json';

function App() {
  const [displayedCoins, setDisplayedCoins] = useState(CoinData)
  const filterResult = (filterKey) => {
    const result = CoinData.filter();
    setDisplayedCoins(result);
  }
  return (
    <div className="App">
        ...search youir coin...
      <br /><br />
      <div className='Box'>
        <h3>filter by time</h3>
        <button onClick={()=>filterResult('this week')}>this week</button>
        <button>this month</button>
        <button>past 3 months</button>
        <button>past 6 months</button>
        <button>past 1 year</button>
        <button>past 3 years</button>
      </div>
      <div className='cards-container'>
        {
          CoinData.map( (coin, i) => (
            <CoinCard coin = { coin } key={ i }/>
        ))}
      </div>
    </div>
  );
}

export default App;
