import { useState } from 'react';
import './App.css';
import CoinCard from './CoinCard'
import CoinData from './MOCK_DATA.json';

function App() {
  const handler = () => {console.log('minfilter:', minFilter, 'maxfilter:', maxFilter, 'pricefiltercoins: '
  , priceFilteredCoins, 'timefiltercoins: ', timeFilteredCoins, 'displayed:', displayedCoins)}

  const [displayedCoins, setDisplayedCoins] = useState(() => CoinData)
  const [priceFilteredCoins, setPriceFilterCoins] = useState(() => CoinData)
  const [timeFilteredCoins, setTimeFilteredCoins] = useState(() => CoinData)

  const [minFilter, setMinFilter] = useState('')
  const [maxFilter, setMaxfilter] = useState('')

  const sortByTime = () => {
    let x = displayedCoins;
    handler();
    x.sort(
      (a, b) => a.created_time - b.created_time
    )
    setDisplayedCoins(x);
    setFinalDisplay();
  }

  const sortByPrice = () => {
    handler();
    setDisplayedCoins(
      displayedCoins.sort(
        (a, b) => {return(a.token_price - b.token_price)}
      )
    );
    setFinalDisplay();
  }

  const setFinalDisplay = () => {
    setDisplayedCoins(() =>{ return(priceFilteredCoins.filter(value => timeFilteredCoins.includes(value)))});
  }

  const PriceFilterResult = (minFilter, maxFilter) => {
    handler();
    const result = CoinData.filter((curData)=>{
      return (curData.token_price <= maxFilter && curData.token_price >= minFilter);
    }
    );
    setPriceFilterCoins(result);
    setFinalDisplay();
  }

  const filterResult = (filterKey) => {
    handler();
    // what is curData?
    const result = CoinData.filter((curData)=>{
      return curData.created_time >= filterKey;
    }
    );
    setTimeFilteredCoins(result);
    setFinalDisplay();
  }

  return (
    <div className="App">
        ...search youir coin...
      <br /><br />

      <div className='priceFilter Box'>
        <h3>filter by price</h3>
        <div>
                <input
                    placeholder="min"
                    value={minFilter}
                    //e = event,
                    onChange={(e) => setMinFilter(e.target.value)}
                /> <br />
                <input
                    placeholder="MAX"
                    value={maxFilter}
                    //e = event,
                    onChange={(e) => setMaxfilter(e.target.value)}
                /> <br />
                <button
                    // src={SearchIcon}
                    alt="search"
                    onClick={()=>PriceFilterResult(minFilter, maxFilter)}
                >go</button>
        </div>
      </div>

      <div className='timeFilter Box'>
        <h3>filter by time</h3>
         {/* why it need double click to change? */}
        <button onClick={()=>filterResult('2022-04-01')}>this month</button>
        <button onClick={()=>filterResult('2022-01-01')}>past 3 months</button>
        <button onClick={()=>filterResult('2021-10-01')}>past 6 months</button>
        <button onClick={()=>filterResult('2021-04-01')}>past 1 year</button>
        <button onClick={()=>filterResult('2019-04-01')}>past 3 years</button>
      </div>

      <div className='sortOption Box'>
        <button onClick={sortByTime}>sort by time</button>
        <button onClick={sortByPrice}>sort by price</button>
      </div>

      <div className='cards-container'>
        {
          displayedCoins.map( (coin, i) => (
            <CoinCard coin = { coin } key={ i }/>
        ))}
      </div>
    </div>
  );
}

export default App;
