import { useState } from 'react';
import './App.css';
import CoinCard from './CoinCard'
import CoinData from './MOCK_DATA.json';

function App() {
  const handler = () => {console.log(displayedCoins)}
  const [searchTerm, setSearchTerm] = useState('');

  // const func_name = (parameter) => {things to do}
  const searchMovies = async (title) => {
      // const response = await fetch(`${API_URL}&s=${title}`);
      // const data = await response.json();

      // setMovies(data.Search);
  }
  const [displayedCoins, setDisplayedCoins] = useState(() => CoinData)
  const filterResult = (filterKey) => {
    handler();
    const result = CoinData.filter((curData)=>{
      return curData.created_time >= filterKey;
    }
    );
    setDisplayedCoins(result);
    console.log(displayedCoins)
  }
  return (
    <div className="App">
        ...search youir coin...
      <br /><br />

      <div className='Box'>
        <h3>filter by price</h3>
        <label htmlFor="min">min:</label>
        <input type="text" id="min" name="min" /><br />
        <label htmlFor="max">max:</label>
        <input type="text" id="max" name="max" /><br />
        <div className="search">
                <input
                    placeholder="search for movie"
                    value={searchTerm}
                    //e = event,
                    onChange={(e) => setSearchTerm(e.target.value)}
                />
                <img
                    // src={SearchIcon}
                    alt="search"
                    onClick={() => searchMovies(searchTerm)}
                />
        </div>
      </div>

      <div className='Box'>
        <h3>filter by time</h3>
        <button onClick={()=>filterResult('2022-04-01')}>this month</button>
        <button onClick={()=>filterResult('2022-01-01')}>past 3 months</button>
        <button onClick={()=>filterResult('2021-10-01')}>past 6 months</button>
        <button onClick={()=>filterResult('2021-04-01')}>past 1 year</button>
        <button onClick={()=>filterResult('2019-04-01')}>past 3 years</button>
      </div>
      <div className='cards-container'>
        {
          displayedCoins.map( (coin, i) => (
            <CoinCard coin = { coin } key={ i }/>
        ))}
      </div>
    </div>
  );
}

export default App;
